import React, { useState, useCallback, useRef } from 'react';
import { Button, Tabs, Tab, Spinner, Table, Alert, Form } from 'react-bootstrap';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import "../App.css";

const API_BASE_URL = 'http://localhost:5000';

const ShapeAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('shape');
  const [error, setError] = useState('');
  const [results, setResults] = useState({
    shape: null,
    color: null
  });

  const fileInputRef = useRef(null);

  const resetState = () => {
    setFile(null);
    setPreview('');
    setError('');
    setResults({
      shape: null,
      color: null
    });
  };

  const onDrop = useCallback((acceptedFiles) => {
    setError('');
    
    if (acceptedFiles.length === 0) {
      return;
    }
    
    const newFile = acceptedFiles[0];
    
    if (!newFile.type.match('image.*')) {
      setError('Please upload an image file.');
      return;
    }
    
    setFile(newFile);
    
    const reader = new FileReader();
    reader.onload = () => {
      setPreview(reader.result);
    };
    reader.readAsDataURL(newFile);
    
    // Reset results when a new file is uploaded
    setResults({
      shape: null,
      color: null
    });
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ 
    onDrop,
    accept: {
      'image/*': []
    },
    maxFiles: 1
  });

  const handleAnalyze = async () => {
    if (!file) {
      setError('Please upload an image first.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const formData = new FormData();
      formData.append('file', file);

      const endpoint = activeTab === 'shape' ? '/detect-size' : '/detect-color';
      
      const response = await axios.post(`${API_BASE_URL}${endpoint}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        setResults({
          ...results,
          [activeTab]: response.data
        });
      } else {
        setError(response.data.message || 'An error occurred during analysis.');
      }
    } catch (err) {
      console.error('Analysis error:', err);
      setError(err.response?.data?.message || 'Failed to analyze the image. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const handleClickUpload = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="shape-analyzer">
      <div className="row mb-4">
        <div className="col-md-6">
          <div 
            {...getRootProps()} 
            className={`upload-area ${isDragActive ? 'drag-active' : ''}`}
            onClick={handleClickUpload}
          >
            <input {...getInputProps()} ref={fileInputRef} />
            {preview ? (
              <div>
                <img src={preview} alt="Preview" className="image-preview" />
                <p className="mt-2">Drag & drop a new image, or click to change</p>
              </div>
            ) : (
              <div>
                <i className="bi bi-cloud-upload" style={{ fontSize: '2rem' }}></i>
                <p>Drag & drop an image here, or click to select</p>
                <p className="text-muted small">Supported formats: JPG, PNG, BMP</p>
              </div>
            )}
          </div>
          
          {file && (
            <div className="text-center mt-2">
              <Button 
                variant="outline-secondary" 
                size="sm" 
                onClick={resetState}
              >
                Remove image
              </Button>
            </div>
          )}
        </div>

        <div className="col-md-6">
          <Tabs
            activeKey={activeTab}
            onSelect={handleTabChange}
            className="mb-3"
          >
            <Tab eventKey="shape" title="Shape & Size">
              <p>Analyze the shape and measure the dimensions of objects in the image.</p>
            </Tab>
            <Tab eventKey="color" title="Color">
              <p>Detect the dominant color of objects in the image.</p>
            </Tab>
          </Tabs>

          {error && <Alert variant="danger">{error}</Alert>}

          <Button
            variant="primary"
            onClick={handleAnalyze}
            disabled={!file || loading}
            className="btn-analyze"
          >
            {loading ? (
              <>
                <Spinner
                  as="span"
                  animation="border"
                  size="sm"
                  role="status"
                  aria-hidden="true"
                  className="mr-2"
                />
                <span className="ml-2">Analyzing...</span>
              </>
            ) : (
              `Analyze ${activeTab === 'shape' ? 'Shape & Size' : 'Color'}`
            )}
          </Button>
        </div>
      </div>

      {/* Results Section */}
      {results[activeTab] && (
        <div className="results-section">
          <h3>Analysis Results</h3>
          
          {activeTab === 'shape' && results.shape && (
            <div className="row">
              <div className="col-md-6">
                <h4>Detected Shape</h4>
                <img 
                  src={`${API_BASE_URL}/uploads/size_${results.shape.filename}`} 
                  alt="Analyzed Shape" 
                  className="result-image"
                />
              </div>
              <div className="col-md-6">
                <h4>Measurements</h4>
                <Table striped bordered hover className="result-table">
                  <thead>
                    <tr>
                      <th>Property</th>
                      <th>Value</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>Shape</td>
                      <td>{results.shape.shape}</td>
                    </tr>
                    <tr>
                      <td>Length</td>
                      <td>{results.shape.length.toFixed(2)} mm</td>
                    </tr>
                    <tr>
                      <td>Breadth</td>
                      <td>{results.shape.breadth.toFixed(2)} mm</td>
                    </tr>
                    <tr>
                      <td>Area</td>
                      <td>{results.shape.area.toFixed(2)} mmÂ²</td>
                    </tr>
                  </tbody>
                </Table>
              </div>
            </div>
          )}

          {activeTab === 'color' && results.color && (
            <div className="row">
              <div className="col-md-6">
                <h4>Detected Color</h4>
                <img 
                  src={`${API_BASE_URL}/uploads/color_${results.color.filename}`} 
                  alt="Analyzed Color" 
                  className="result-image"
                />
              </div>
              <div className="col-md-6">
                <h4>Color Information</h4>
                <Table striped bordered hover className="result-table">
                  <thead>
                    <tr>
                      <th>Property</th>
                      <th>Value</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>Color Name</td>
                      <td>{results.color.color}</td>
                    </tr>
                    <tr>
                      <td>Shape</td>
                      <td>{results.color.shape}</td>
                    </tr>
                    <tr>
                      <td>RGB Value</td>
                      <td>
                        <div className="d-flex align-items-center">
                          <div 
                            style={{
                              width: '20px', 
                              height: '20px', 
                              backgroundColor: `rgb(${results.color.rgb.join(',')})`,
                              border: '1px solid #ddd',
                              marginRight: '10px'
                            }}
                          ></div>
                          RGB({results.color.rgb.join(', ')})
                        </div>
                      </td>
                    </tr>
                  </tbody>
                </Table>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ShapeAnalyzer;
