import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Alert,
  Tabs,
  Tab,
} from "react-bootstrap";
import FileUpload from "./ui/FileUpload";
import axios from "axios";

const API_BASE_URL =
  process.env.REACT_APP_API_BASE_URL || "http://localhost:5000";

const ShapeAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [originalImage, setOriginalImage] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState("shape");

  const handleFileUpload = (uploadedFile) => {
    setFile(uploadedFile);
    setOriginalImage(URL.createObjectURL(uploadedFile));
    setProcessedImage(null);
    setResult(null);
    setError(null);
  };

  const handleDetection = async (type) => {
    if (!file) {
      setError("Please upload an image first");
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const endpoint = type === "shape" ? "/detect-shape" : "/detect-color";
      const response = await axios.post(
        `${API_BASE_URL}${endpoint}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data.success) {
        setResult(response.data);

        // Create URL for the processed image
        const processedImageUrl = `${API_BASE_URL}/uploads/${response.data.processed_image}`;
        setProcessedImage(processedImageUrl);
      } else {
        setError(response.data.message || "An unknown error occurred");
      }
    } catch (err) {
      console.error("Detection error:", err);
      setError(
        err.response?.data?.message ||
          "Failed to process the image. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  const renderResults = () => {
    if (!result) return null;

    if (activeTab === "shape") {
      return (
        <div className="mt-4">
          <h5>Shape Analysis Results</h5>
          <div className="result-details p-3 bg-light rounded">
            <p>
              <strong>Shape:</strong> {result.shape}
            </p>
            {result.length && result.breadth && (
              <>
                <p>
                  <strong>Dimensions:</strong> {result.length.toFixed(2)} mm ×{" "}
                  {result.breadth.toFixed(2)} mm
                </p>
                <p>
                  <strong>Area:</strong>{" "}
                  {result.area ? result.area.toFixed(2) : "-"} mm²
                </p>
              </>
            )}
            {result.radius && (
              <>
                <p>
                  <strong>Radius:</strong> {result.radius.toFixed(2)} mm
                </p>
                <p>
                  <strong>Area:</strong>{" "}
                  {result.area ? result.area.toFixed(2) : "-"} mm²
                </p>
              </>
            )}
          </div>
        </div>
      );
    } else {
      return (
        <div className="mt-4">
          <h5>Color Analysis Results</h5>
          <div className="result-details p-3 bg-light rounded">
            <p>
              <strong>Dominant Color:</strong>{" "}
              <span
                className="color-sample d-inline-block"
                style={{
                  backgroundColor: result.color_hex || "#FFFFFF",
                  width: "20px",
                  height: "20px",
                  border: "1px solid #ccc",
                  marginRight: "5px",
                  verticalAlign: "middle",
                  borderRadius: "3px",
                }}
              ></span>
              {result.color_name}
            </p>
            {result.color_hex && (
              <p>
                <strong>Hex Code:</strong> {result.color_hex}
              </p>
            )}
            {result.rgb && (
              <p>
                <strong>RGB:</strong> ({result.rgb.join(", ")})
              </p>
            )}
          </div>
        </div>
      );
    }
  };

  return (
    <Container className="py-4">
      <h2 className="text-center mb-4">SHAPE Analyzer</h2>

      <Row className="mb-4">
        <Col md={6} className="mb-4 mb-md-0">
          <Card className="h-100 shadow-sm">
            <Card.Header>
              <h5 className="mb-0">Upload Image</h5>
            </Card.Header>
            <Card.Body>
              <FileUpload
                onFileUpload={handleFileUpload}
                fileUrl={originalImage}
                title="Upload Image"
                description="Upload a clear image of a shape on a contrasting background"
              />
            </Card.Body>
          </Card>
        </Col>

        <Col md={6}>
          <Card className="h-100 shadow-sm">
            <Card.Header>
              <Tabs
                activeKey={activeTab}
                onSelect={(k) => setActiveTab(k)}
                className="border-bottom-0"
              >
                <Tab eventKey="shape" title="Shape Detection" />
                <Tab eventKey="color" title="Color Detection" />
              </Tabs>
            </Card.Header>
            <Card.Body>
              <p className="mb-3">
                {activeTab === "shape"
                  ? "Detect the shape and measure its dimensions"
                  : "Identify the dominant color of the object"}
              </p>

              <Button
                variant="primary"
                className="w-100"
                onClick={() => handleDetection(activeTab)}
                disabled={loading || !file}
              >
                {loading ? (
                  <>
                    <Spinner
                      as="span"
                      animation="border"
                      size="sm"
                      className="me-2"
                    />
                    Processing...
                  </>
                ) : (
                  `Detect ${activeTab === "shape" ? "Shape" : "Color"}`
                )}
              </Button>

              {error && (
                <Alert variant="danger" className="mt-3">
                  {error}
                </Alert>
              )}

              {renderResults()}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {processedImage && (
        <Row>
          <Col>
            <Card className="shadow-sm">
              <Card.Header>
                <h5 className="mb-0">Analysis Result</h5>
              </Card.Header>
              <Card.Body className="text-center">
                <img
                  src={processedImage}
                  alt="Processed"
                  className="img-fluid rounded"
                  style={{ maxHeight: "400px" }}
                />
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default ShapeAnalyzer;
