import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Alert,
  Tabs,
  Tab,
  Form,
} from "react-bootstrap";
import FileUpload from "./ui/FileUpload";
import axios from "axios";

const API_BASE_URL =
  process.env.REACT_APP_API_BASE_URL || "http://localhost:5000";

const ShapeAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("upload");
  const [results, setResults] = useState({
    shape: null,
    size: null,
    color: null,
    combined: null,
  });

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(selectedFile);
      setActiveTab("upload");
      // Reset results when a new file is selected
      setResults({
        shape: null,
        size: null,
        color: null,
        combined: null,
      });
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      setFile(droppedFile);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(droppedFile);
      setActiveTab("upload");
      // Reset results
      setResults({
        shape: null,
        size: null,
        color: null,
        combined: null,
      });
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const processImage = async (endpoint) => {
    if (!file) {
      setError("Please select an image first");
      return;
    }

    setLoading(true);
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post(
        `${API_BASE_URL}/${endpoint}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data.success) {
        // Update the specific result based on the endpoint
        setResults((prevResults) => ({
          ...prevResults,
          [endpoint.split("-")[0]]: response.data,
        }));
        setActiveTab(endpoint.split("-")[0]);
      } else {
        setError(response.data.message || "Error processing image");
      }
    } catch (err) {
      console.error("Error:", err);
      setError("Failed to process the image. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const renderShapeResult = () => {
    if (!results.shape) return null;

    return (
      <div>
        <h4>Detected Shape: {results.shape.shape}</h4>
        <img
          src={`${API_BASE_URL}/uploads/${results.shape.processed_image}`}
          alt="Processed"
          className="image-preview mt-3"
        />
      </div>
    );
  };

  const renderSizeResult = () => {
    if (!results.size) return null;

    return (
      <div>
        <h4>Size Measurements</h4>
        <Row>
          <Col md={6}>
            <Card className="shape-card">
              <Card.Body>
                <Card.Title>Dimensions</Card.Title>
                <p>Length: {results.size.length.toFixed(2)} mm</p>
                <p>Breadth: {results.size.breadth.toFixed(2)} mm</p>
                <p>Area: {results.size.area.toFixed(2)} mm²</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={6}>
            <img
              src={`${API_BASE_URL}/uploads/${results.size.processed_image}`}
              alt="Size Measurement"
              className="image-preview"
            />
          </Col>
        </Row>
      </div>
    );
  };

  const renderColorResult = () => {
    if (!results.color) return null;

    return (
      <div>
        <h4>Color Analysis</h4>
        <Row>
          <Col md={6}>
            <Card className="shape-card">
              <Card.Body>
                <Card.Title>Dominant Color</Card.Title>
                <div className="d-flex align-items-center">
                  <span
                    className="color-indicator"
                    style={{
                      backgroundColor: `rgb(${results.color.color.r}, ${results.color.color.g}, ${results.color.color.b})`,
                    }}
                  ></span>
                  <span>{results.color.color_name}</span>
                </div>
                <p className="mt-2">
                  RGB: ({results.color.color.r}, {results.color.color.g},{" "}
                  {results.color.color.b})
                </p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={6}>
            <img
              src={`${API_BASE_URL}/uploads/${results.color.processed_image}`}
              alt="Color Analysis"
              className="image-preview"
            />
          </Col>
        </Row>
      </div>
    );
  };

  const renderCombinedResult = () => {
    if (!results.combined) return null;

    return (
      <div>
        <h4>Combined Analysis</h4>
        <Row>
          <Col md={6}>
            <Card className="shape-card">
              <Card.Body>
                <Card.Title>Shape Information</Card.Title>
                <p>Detected Shape: {results.combined.shape}</p>
                <p>Length: {results.combined.length.toFixed(2)} mm</p>
                <p>Breadth: {results.combined.breadth.toFixed(2)} mm</p>
                <p>Area: {results.combined.area.toFixed(2)} mm²</p>

                <div className="mt-3">
                  <Card.Title>Color Information</Card.Title>
                  <div className="d-flex align-items-center">
                    <span
                      className="color-indicator"
                      style={{
                        backgroundColor: `rgb(${results.combined.color.r}, ${results.combined.color.g}, ${results.combined.color.b})`,
                      }}
                    ></span>
                    <span>{results.combined.color_name}</span>
                  </div>
                  <p className="mt-2">
                    RGB: ({results.combined.color.r}, {results.combined.color.g}
                    , {results.combined.color.b})
                  </p>
                </div>
              </Card.Body>
            </Card>
          </Col>
          <Col md={6}>
            <img
              src={`${API_BASE_URL}/uploads/${results.combined.processed_image}`}
              alt="Combined Analysis"
              className="image-preview"
            />
          </Col>
        </Row>
      </div>
    );
  };

  return (
    <Container>
      <h2 className="mb-4 text-center">Shape, Size & Color Analyzer</h2>

      {error && (
        <Alert variant="danger" onClose={() => setError("")} dismissible>
          {error}
        </Alert>
      )}

      <Row className="mb-4">
        <Col md={12}>
          <Card>
            <Card.Body>
              <div
                className={`upload-area ${preview ? "active" : ""}`}
                onDrop={handleDrop}
                onDragOver={handleDragOver}
              >
                {!preview ? (
                  <div>
                    <p className="mb-2">
                      Drag & drop an image here or click to select
                    </p>
                    <Form.Control
                      type="file"
                      onChange={handleFileChange}
                      accept="image/*"
                      style={{ display: "none" }}
                      id="file-input"
                    />
                    <Button
                      variant="primary"
                      onClick={() =>
                        document.getElementById("file-input").click()
                      }
                    >
                      Select Image
                    </Button>
                  </div>
                ) : (
                  <div className="position-relative">
                    <img
                      src={preview}
                      alt="Preview"
                      className="image-preview"
                    />
                    <div className="mt-3">
                      <Button
                        variant="outline-secondary"
                        className="me-2"
                        onClick={() => {
                          setFile(null);
                          setPreview("");
                        }}
                      >
                        Remove
                      </Button>
                      <Form.Control
                        type="file"
                        onChange={handleFileChange}
                        accept="image/*"
                        style={{ display: "none" }}
                        id="file-input-change"
                      />
                      <Button
                        variant="outline-primary"
                        onClick={() =>
                          document.getElementById("file-input-change").click()
                        }
                      >
                        Change Image
                      </Button>
                    </div>
                  </div>
                )}
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {preview && (
        <Row className="mb-4">
          <Col md={12}>
            <Card>
              <Card.Body>
                <h4>Analysis Options</h4>
                <div className="d-flex justify-content-between flex-wrap">
                  <Button
                    variant="primary"
                    onClick={() => processImage("shape-detect")}
                    disabled={loading}
                    className="mb-2"
                  >
                    {loading && activeTab === "shape" ? (
                      <>
                        <Spinner
                          as="span"
                          animation="border"
                          size="sm"
                          role="status"
                          aria-hidden="true"
                        />
                        <span className="ms-2">Processing...</span>
                      </>
                    ) : (
                      "Detect Shape"
                    )}
                  </Button>

                  <Button
                    variant="success"
                    onClick={() => processImage("size-detect")}
                    disabled={loading}
                    className="mb-2"
                  >
                    {loading && activeTab === "size" ? (
                      <>
                        <Spinner
                          as="span"
                          animation="border"
                          size="sm"
                          role="status"
                          aria-hidden="true"
                        />
                        <span className="ms-2">Processing...</span>
                      </>
                    ) : (
                      "Measure Size"
                    )}
                  </Button>

                  <Button
                    variant="info"
                    onClick={() => processImage("color-detect")}
                    disabled={loading}
                    className="mb-2"
                  >
                    {loading && activeTab === "color" ? (
                      <>
                        <Spinner
                          as="span"
                          animation="border"
                          size="sm"
                          role="status"
                          aria-hidden="true"
                        />
                        <span className="ms-2">Processing...</span>
                      </>
                    ) : (
                      "Analyze Color"
                    )}
                  </Button>

                  <Button
                    variant="dark"
                    onClick={() => processImage("combined-detect")}
                    disabled={loading}
                    className="mb-2"
                  >
                    {loading && activeTab === "combined" ? (
                      <>
                        <Spinner
                          as="span"
                          animation="border"
                          size="sm"
                          role="status"
                          aria-hidden="true"
                        />
                        <span className="ms-2">Processing...</span>
                      </>
                    ) : (
                      "Complete Analysis"
                    )}
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}

      {(results.shape || results.size || results.color || results.combined) && (
        <Row>
          <Col md={12}>
            <Card>
              <Card.Body>
                <Tabs
                  activeKey={activeTab}
                  onSelect={(k) => setActiveTab(k)}
                  className="mb-3"
                >
                  <Tab eventKey="upload" title="Upload" disabled={true}>
                    {/* Upload tab is just a placeholder */}
                  </Tab>

                  <Tab eventKey="shape" title="Shape" disabled={!results.shape}>
                    {renderShapeResult()}
                  </Tab>

                  <Tab eventKey="size" title="Size" disabled={!results.size}>
                    {renderSizeResult()}
                  </Tab>

                  <Tab eventKey="color" title="Color" disabled={!results.color}>
                    {renderColorResult()}
                  </Tab>

                  <Tab
                    eventKey="combined"
                    title="Complete"
                    disabled={!results.combined}
                  >
                    {renderCombinedResult()}
                  </Tab>
                </Tabs>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default ShapeAnalyzer;
