import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Form,
  Tabs,
  Tab,
  Spinner,
  Table,
  Alert,
} from "react-bootstrap";
import axios from "axios";
import "../App.css";

const ShapeAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("size");
  const [results, setResults] = useState({ size: null, color: null });
  const [error, setError] = useState("");
  const [dragActive, setDragActive] = useState(false);

  const handleFile = (file) => {
    setFile(file);
    setError("");

    // Create preview
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleTabSelect = (key) => {
    setActiveTab(key);
  };

  const analyzeImage = async () => {
    if (!file) {
      setError("Please select an image first");
      return;
    }

    setLoading(true);
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      let response;
      const endpoint = activeTab === "size" ? "/detect-shape" : "/detect-color";

      response = await axios.post(
        `http://localhost:5000${endpoint}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data.success) {
        setResults({
          ...results,
          [activeTab]: response.data,
        });
      } else {
        setError(response.data.message || "An error occurred during analysis");
      }
    } catch (err) {
      console.error("Error during analysis:", err);
      setError(
        "Failed to communicate with the server. Please try again later."
      );
    } finally {
      setLoading(false);
    }
  };

  const renderSizeResults = () => {
    const data = results.size;
    if (!data) return null;

    return (
      <div className="results-container">
        <h4>Shape Analysis Results</h4>
        <Row>
          <Col md={6}>
            <img
              src={`http://localhost:5000/uploads/size_${data.filename}`}
              alt="Processed with measurements"
              className="results-image"
            />
          </Col>
          <Col md={6}>
            <Table striped bordered hover className="results-table">
              <thead>
                <tr>
                  <th>Property</th>
                  <th>Value</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Shape</td>
                  <td>{data.shape}</td>
                </tr>
                <tr>
                  <td>Length</td>
                  <td>{data.length_mm?.toFixed(2)} mm</td>
                </tr>
                <tr>
                  <td>Width</td>
                  <td>{data.breadth_mm?.toFixed(2)} mm</td>
                </tr>
                <tr>
                  <td>Area</td>
                  <td>{data.area_mm?.toFixed(2)} mm²</td>
                </tr>
              </tbody>
            </Table>
          </Col>
        </Row>
      </div>
    );
  };

  const renderColorResults = () => {
    const data = results.color;
    if (!data) return null;

    return (
      <div className="results-container">
        <h4>Color Analysis Results</h4>
        <Row>
          <Col md={6}>
            <img
              src={`http://localhost:5000/uploads/color_${data.filename}`}
              alt="Processed with color detection"
              className="results-image"
            />
          </Col>
          <Col md={6}>
            <Table striped bordered hover className="results-table">
              <thead>
                <tr>
                  <th>Shape</th>
                  <th>Color</th>
                </tr>
              </thead>
              <tbody>
                {data.shape_colors?.map((item, index) => (
                  <tr key={index}>
                    <td>{item.shape}</td>
                    <td>
                      <div
                        className="color-box"
                        style={{
                          backgroundColor: `rgb(${item.rgb_values.join(",")})`,
                        }}
                      ></div>
                      {item.color_name}
                      (RGB: {item.rgb_values.join(", ")})
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </Col>
        </Row>
      </div>
    );
  };

  return (
    <Container>
      <div
        className={`upload-area ${dragActive ? "drag-active" : ""}`}
        onDragEnter={handleDrag}
        onDragOver={handleDrag}
        onDragLeave={handleDrag}
        onDrop={handleDrop}
      >
        <h4>Upload an Image</h4>
        <p>Drag and drop an image here or click to browse</p>

        <Form.Group>
          <Form.Control
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            style={{ display: "none" }}
            id="fileInput"
          />
          <Button
            variant="outline-primary"
            onClick={() => document.getElementById("fileInput").click()}
            className="btn-upload"
          >
            Browse Files
          </Button>
        </Form.Group>

        {preview && (
          <div className="preview-container">
            <img src={preview} alt="Preview" className="image-preview" />
          </div>
        )}
      </div>

      <Tabs
        activeKey={activeTab}
        onSelect={handleTabSelect}
        id="analysis-tabs"
        className="mb-3"
      >
        <Tab eventKey="size" title="Size Analysis">
          <p>Detect shapes and measure their dimensions</p>
        </Tab>
        <Tab eventKey="color" title="Color Analysis">
          <p>Detect shapes and identify their colors</p>
        </Tab>
      </Tabs>

      {error && (
        <Alert variant="danger" className="error-message">
          {error}
        </Alert>
      )}

      <div className="text-center mb-4">
        <Button
          variant="primary"
          onClick={analyzeImage}
          disabled={loading || !file}
        >
          {loading ? (
            <>
              <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
                className="me-2"
              />
              Analyzing...
            </>
          ) : (
            `Analyze ${activeTab === "size" ? "Size" : "Color"}`
          )}
        </Button>
      </div>

      {loading && (
        <div className="spinner-container">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
        </div>
      )}

      {activeTab === "size" ? renderSizeResults() : renderColorResults()}
    </Container>
  );
};

export default ShapeAnalyzer;
