import React, { useState, useRef } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  Tabs,
  Tab,
  Spinner,
  Alert,
  Table,
} from "react-bootstrap";
import "../App.css";

const API_URL = "http://localhost:5000";

const ShapeAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState("");
  const [isDragging, setIsDragging] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("size");
  const [results, setResults] = useState({
    size: null,
    color: null,
  });

  const fileInputRef = useRef(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      processFile(selectedFile);
    }
  };

  const processFile = (selectedFile) => {
    if (!selectedFile.type.match("image.*")) {
      setError("Please select an image file (JPEG, PNG, etc.)");
      return;
    }

    if (selectedFile.size > 10 * 1024 * 1024) {
      setError("File size must be less than 10MB");
      return;
    }

    setFile(selectedFile);
    setError("");
    setResults({ size: null, color: null });

    // Create preview URL
    const reader = new FileReader();
    reader.onload = () => setPreviewUrl(reader.result);
    reader.readAsDataURL(selectedFile);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);

    if (e.dataTransfer.files.length) {
      processFile(e.dataTransfer.files[0]);
    }
  };

  const detectShape = async (type) => {
    if (!file) {
      setError("Please select an image first");
      return;
    }

    setIsLoading(true);
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      let endpoint = "";
      if (type === "size") {
        endpoint = "/detect-shape";
      } else if (type === "color") {
        endpoint = "/detect-color";
      }

      const response = await fetch(`${API_URL}${endpoint}`, {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Failed to process image");
      }

      setResults((prev) => ({
        ...prev,
        [type]: data,
      }));
      setActiveTab(type);
    } catch (error) {
      console.error("Error:", error);
      setError(error.message || "An error occurred during processing");
    } finally {
      setIsLoading(false);
    }
  };

  const clearImage = () => {
    setFile(null);
    setPreviewUrl("");
    setResults({ size: null, color: null });
    setError("");
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const renderSizeResults = () => {
    if (!results.size) return null;

    return (
      <div className="results-container">
        <Row>
          <Col md={6}>
            <Card className="mb-3">
              <Card.Body>
                <Card.Title>Detected Shape</Card.Title>
                <div className="text-center">
                  <img
                    src={`${API_URL}/uploads/size_${file.name}`}
                    alt="Detected shape with measurements"
                    className="result-image"
                  />
                </div>
              </Card.Body>
            </Card>
          </Col>

          <Col md={6}>
            <Card>
              <Card.Body>
                <Card.Title>Measurements</Card.Title>
                <Table striped bordered hover>
                  <thead>
                    <tr>
                      <th>Property</th>
                      <th>Value</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>Shape</td>
                      <td>{results.size.shape}</td>
                    </tr>
                    <tr>
                      <td>Length</td>
                      <td>{results.size.length.toFixed(2)} mm</td>
                    </tr>
                    {results.size.breadth && (
                      <tr>
                        <td>Breadth</td>
                        <td>{results.size.breadth.toFixed(2)} mm</td>
                      </tr>
                    )}
                    {results.size.radius && (
                      <tr>
                        <td>Radius</td>
                        <td>{results.size.radius.toFixed(2)} mm</td>
                      </tr>
                    )}
                    <tr>
                      <td>Area</td>
                      <td>{results.size.area.toFixed(2)} mmÂ²</td>
                    </tr>
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </div>
    );
  };

  const renderColorResults = () => {
    if (!results.color) return null;

    return (
      <div className="results-container">
        <Row>
          <Col md={6}>
            <Card className="mb-3">
              <Card.Body>
                <Card.Title>Detected Colors</Card.Title>
                <div className="text-center">
                  <img
                    src={`${API_URL}/uploads/color_${file.name}`}
                    alt="Detected color"
                    className="result-image"
                  />
                </div>
              </Card.Body>
            </Card>
          </Col>

          <Col md={6}>
            <Card>
              <Card.Body>
                <Card.Title>Color Information</Card.Title>
                <Table striped bordered hover>
                  <thead>
                    <tr>
                      <th>Color</th>
                      <th>RGB Value</th>
                      <th>Percentage</th>
                    </tr>
                  </thead>
                  <tbody>
                    {results.color.colors.map((color, index) => (
                      <tr key={index}>
                        <td>
                          <div
                            className="color-swatch"
                            style={{
                              backgroundColor: `rgb(${color.rgb.join(",")})`,
                            }}
                          />
                          {color.name}
                        </td>
                        <td>{color.rgb.join(", ")}</td>
                        <td>{(color.percentage * 100).toFixed(2)}%</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
                <p className="mt-3">
                  <strong>Shape:</strong> {results.color.shape || "Unknown"}
                </p>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </div>
    );
  };

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <Card>
            <Card.Body>
              <Card.Title>Upload Image</Card.Title>
              <div
                className={`upload-card ${isDragging ? "drag-active" : ""}`}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
              >
                {!file ? (
                  <div>
                    <p>Drag & drop an image or click to select</p>
                    <Button
                      variant="primary"
                      onClick={() => fileInputRef.current.click()}
                    >
                      Select Image
                    </Button>
                    <input
                      type="file"
                      ref={fileInputRef}
                      onChange={handleFileChange}
                      accept="image/*"
                      style={{ display: "none" }}
                    />
                  </div>
                ) : (
                  <div className="preview-container">
                    <img
                      src={previewUrl}
                      alt="Preview"
                      className="preview-image"
                    />
                    <div className="mt-3">
                      <Button
                        variant="outline-secondary"
                        onClick={clearImage}
                        className="me-2"
                      >
                        Clear
                      </Button>
                      <Button
                        variant="primary"
                        onClick={() => detectShape("size")}
                        disabled={isLoading}
                        className="me-2"
                      >
                        {isLoading && activeTab === "size" ? (
                          <>
                            <Spinner
                              as="span"
                              animation="border"
                              size="sm"
                              role="status"
                              aria-hidden="true"
                            />
                            <span className="ms-2">Processing...</span>
                          </>
                        ) : (
                          "Detect Size"
                        )}
                      </Button>
                      <Button
                        variant="primary"
                        onClick={() => detectShape("color")}
                        disabled={isLoading}
                      >
                        {isLoading && activeTab === "color" ? (
                          <>
                            <Spinner
                              as="span"
                              animation="border"
                              size="sm"
                              role="status"
                              aria-hidden="true"
                            />
                            <span className="ms-2">Processing...</span>
                          </>
                        ) : (
                          "Detect Color"
                        )}
                      </Button>
                    </div>
                  </div>
                )}
              </div>

              {error && (
                <Alert variant="danger" className="mt-3">
                  {error}
                </Alert>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {(results.size || results.color) && (
        <Row>
          <Col>
            <Tabs
              activeKey={activeTab}
              onSelect={(k) => setActiveTab(k)}
              className="mb-3"
            >
              <Tab
                eventKey="size"
                title="Size Detection"
                disabled={!results.size}
              >
                {renderSizeResults()}
              </Tab>
              <Tab
                eventKey="color"
                title="Color Detection"
                disabled={!results.color}
              >
                {renderColorResults()}
              </Tab>
            </Tabs>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default ShapeAnalyzer;
