import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Alert,
  Spinner,
  Card,
  Table,
} from "react-bootstrap";
import axios from "axios";

const ShapeAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [shapeResult, setShapeResult] = useState(null);
  const [colorResult, setColorResult] = useState(null);
  const [sizeResult, setSizeResult] = useState(null);
  const [activeAnalysis, setActiveAnalysis] = useState("shape"); // 'shape', 'color', 'size'

  // API URL - change this to match your backend
  const API_URL = "http://localhost:5000";

  const handleDrop = (e) => {
    e.preventDefault();
    handleFiles(e.dataTransfer.files);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleFileInput = (e) => {
    handleFiles(e.target.files);
  };

  const handleFiles = (files) => {
    if (files.length) {
      const selectedFile = files[0];
      // Reset previous results
      setShapeResult(null);
      setColorResult(null);
      setSizeResult(null);
      setError("");

      // Only accept image files
      if (!selectedFile.type.match("image.*")) {
        setError("Please select an image file");
        return;
      }

      setFile(selectedFile);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => setPreview(e.target.result);
      reader.readAsDataURL(selectedFile);
    }
  };

  const analyzeShape = async () => {
    if (!file) {
      setError("Please select an image first");
      return;
    }

    setLoading(true);
    setError("");
    setActiveAnalysis("shape");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post(`${API_URL}/detect-shape`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.data.success) {
        setShapeResult(response.data);
      } else {
        setError(response.data.message || "Shape detection failed");
      }
    } catch (err) {
      console.error("Error analyzing shape:", err);
      setError("Error connecting to the server. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const analyzeColor = async () => {
    if (!file) {
      setError("Please select an image first");
      return;
    }

    setLoading(true);
    setError("");
    setActiveAnalysis("color");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post(`${API_URL}/detect-color`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.data.success) {
        setColorResult(response.data);
      } else {
        setError(response.data.message || "Color detection failed");
      }
    } catch (err) {
      console.error("Error analyzing color:", err);
      setError("Error connecting to the server. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const analyzeSize = async () => {
    if (!file) {
      setError("Please select an image first");
      return;
    }

    setLoading(true);
    setError("");
    setActiveAnalysis("size");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post(`${API_URL}/detect-size`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.data.success) {
        setSizeResult(response.data);
      } else {
        setError(response.data.message || "Size detection failed");
      }
    } catch (err) {
      console.error("Error analyzing size:", err);
      setError("Error connecting to the server. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const renderResults = () => {
    if (loading) {
      return (
        <div className="text-center my-4">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
          <p className="mt-2">Processing image...</p>
        </div>
      );
    }

    if (activeAnalysis === "shape" && shapeResult) {
      return (
        <div className="results-container">
          <h4>Shape Analysis Results</h4>
          <Row>
            <Col md={6}>
              <Card>
                <Card.Body>
                  <Card.Title>Detected Shape</Card.Title>
                  <p>
                    <strong>Shape Type:</strong> {shapeResult.shape}
                  </p>
                  {shapeResult.area && (
                    <p>
                      <strong>Approximate Area:</strong>{" "}
                      {shapeResult.area.toFixed(2)} px²
                    </p>
                  )}
                  {shapeResult.confidence && (
                    <p>
                      <strong>Confidence:</strong>{" "}
                      {(shapeResult.confidence * 100).toFixed(1)}%
                    </p>
                  )}
                </Card.Body>
              </Card>
            </Col>
            <Col md={6}>
              {shapeResult.image_url && (
                <img
                  src={`${API_URL}/${shapeResult.image_url}`}
                  alt="Shape detection result"
                  className="result-image"
                />
              )}
            </Col>
          </Row>
        </div>
      );
    }

    if (activeAnalysis === "color" && colorResult) {
      return (
        <div className="results-container">
          <h4>Color Analysis Results</h4>
          <Row>
            <Col md={6}>
              <Card>
                <Card.Body>
                  <Card.Title>Detected Colors</Card.Title>
                  <Table striped bordered>
                    <thead>
                      <tr>
                        <th>Color</th>
                        <th>Name</th>
                        <th>Percentage</th>
                      </tr>
                    </thead>
                    <tbody>
                      {colorResult.colors &&
                        colorResult.colors.map((color, index) => (
                          <tr key={index}>
                            <td>
                              <div
                                className="color-badge"
                                style={{
                                  backgroundColor: `rgb(${color.rgb.join(
                                    ","
                                  )})`,
                                }}
                              ></div>
                            </td>
                            <td>{color.name}</td>
                            <td>{(color.percentage * 100).toFixed(1)}%</td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Col>
            <Col md={6}>
              {colorResult.image_url && (
                <img
                  src={`${API_URL}/${colorResult.image_url}`}
                  alt="Color detection result"
                  className="result-image"
                />
              )}
            </Col>
          </Row>
        </div>
      );
    }

    if (activeAnalysis === "size" && sizeResult) {
      return (
        <div className="results-container">
          <h4>Size Analysis Results</h4>
          <Row>
            <Col md={6}>
              <Card>
                <Card.Body>
                  <Card.Title>Measured Dimensions</Card.Title>
                  <Table striped bordered>
                    <thead>
                      <tr>
                        <th>Dimension</th>
                        <th>Value</th>
                      </tr>
                    </thead>
                    <tbody>
                      {sizeResult.length && (
                        <tr>
                          <td>Length</td>
                          <td>{sizeResult.length.toFixed(2)} mm</td>
                        </tr>
                      )}
                      {sizeResult.width && (
                        <tr>
                          <td>Width</td>
                          <td>{sizeResult.width.toFixed(2)} mm</td>
                        </tr>
                      )}
                      {sizeResult.area && (
                        <tr>
                          <td>Area</td>
                          <td>{sizeResult.area.toFixed(2)} mm²</td>
                        </tr>
                      )}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Col>
            <Col md={6}>
              {sizeResult.image_url && (
                <img
                  src={`${API_URL}/${sizeResult.image_url}`}
                  alt="Size measurement result"
                  className="result-image"
                />
              )}
            </Col>
          </Row>
        </div>
      );
    }

    return null;
  };

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <div
            className="dropzone"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onClick={() => document.getElementById("file-input").click()}
          >
            {preview ? (
              <img src={preview} alt="Preview" className="preview-image" />
            ) : (
              <>
                <p>Drag and drop an image here, or click to select a file</p>
                <Button variant="outline-primary">Select Image</Button>
              </>
            )}
            <input
              id="file-input"
              type="file"
              accept="image/*"
              onChange={handleFileInput}
              style={{ display: "none" }}
            />
          </div>
        </Col>
      </Row>

      {error && (
        <Row className="mb-3">
          <Col>
            <Alert variant="danger">{error}</Alert>
          </Col>
        </Row>
      )}

      <Row className="action-buttons">
        <Col>
          <Button
            variant={activeAnalysis === "shape" ? "primary" : "outline-primary"}
            onClick={analyzeShape}
            disabled={loading || !file}
          >
            Detect Shape
          </Button>{" "}
          <Button
            variant={activeAnalysis === "color" ? "primary" : "outline-primary"}
            onClick={analyzeColor}
            disabled={loading || !file}
          >
            Analyze Colors
          </Button>{" "}
          <Button
            variant={activeAnalysis === "size" ? "primary" : "outline-primary"}
            onClick={analyzeSize}
            disabled={loading || !file}
          >
            Measure Size
          </Button>
        </Col>
      </Row>

      {renderResults()}
    </Container>
  );
};

export default ShapeAnalyzer;
