import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Alert,
} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import ImageUploader from "./components/ImageUploader";
import ImageDisplay from "./components/ImageDisplay";
import ShapeResult from "./components/ShapeResult";
import SizeResult from "./components/SizeResult";
import axios from "axios";

function App() {
  const [file, setFile] = useState(null);
  const [filename, setFilename] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [processedImageUrl, setProcessedImageUrl] = useState("");
  const [shapes, setShapes] = useState([]);
  const [measurements, setMeasurements] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("upload"); // 'upload', 'shape', or 'size'

  const handleFileUpload = async (file) => {
    setFile(file);
    setProcessedImageUrl("");
    setShapes([]);
    setMeasurements([]);
    setError("");

    const fileUrl = URL.createObjectURL(file);
    setImageUrl(fileUrl);

    try {
      setLoading(true);
      setActiveTab("upload");

      const formData = new FormData();
      formData.append("image", file);

      const response = await axios.post("/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setFilename(response.data.filename);
      setLoading(false);
    } catch (err) {
      setError("Error uploading image. Please try again.");
      setLoading(false);
    }
  };

  const handleDetectShape = async () => {
    if (!filename) {
      setError("Please upload an image first");
      return;
    }

    try {
      setLoading(true);
      setActiveTab("shape");
      setProcessedImageUrl("");
      setShapes([]);
      setError("");

      const response = await axios.post("/detect-shape", { filename });
      setShapes(response.data.shapes);
      setProcessedImageUrl(response.data.processedImage);
      setLoading(false);
    } catch (err) {
      setError("Error detecting shapes. Please try again.");
      setLoading(false);
    }
  };

  const handleDetectSize = async () => {
    if (!filename) {
      setError("Please upload an image first");
      return;
    }

    try {
      setLoading(true);
      setActiveTab("size");
      setProcessedImageUrl("");
      setMeasurements([]);
      setError("");

      const response = await axios.post("/detect-size", { filename });
      setMeasurements(response.data.measurements);
      setProcessedImageUrl(response.data.processedImage);
      setLoading(false);
    } catch (err) {
      setError("Error detecting sizes. Please try again.");
      setLoading(false);
    }
  };

  return (
    <Container className="mt-5">
      <Card className="shadow-sm">
        <Card.Header className="bg-primary text-white">
          <h2 className="text-center mb-0">Shape and Size Detector</h2>
        </Card.Header>
        <Card.Body>
          <Row className="mb-4">
            <Col md={12} className="d-flex justify-content-center">
              <div className="action-buttons">
                <Button
                  variant={
                    activeTab === "upload" ? "primary" : "outline-primary"
                  }
                  size="lg"
                  className="me-2"
                  onClick={() => setActiveTab("upload")}
                >
                  <i className="bi bi-upload me-2"></i>
                  Upload Image
                </Button>
                <Button
                  variant={
                    activeTab === "shape" ? "primary" : "outline-primary"
                  }
                  size="lg"
                  className="me-2"
                  onClick={handleDetectShape}
                  disabled={!filename}
                >
                  <i className="bi bi-triangle me-2"></i>
                  Detect Shape
                </Button>
                <Button
                  variant={activeTab === "size" ? "primary" : "outline-primary"}
                  size="lg"
                  onClick={handleDetectSize}
                  disabled={!filename}
                >
                  <i className="bi bi-rulers me-2"></i>
                  Detect Size
                </Button>
              </div>
            </Col>
          </Row>

          {error && (
            <Alert variant="danger" onClose={() => setError("")} dismissible>
              {error}
            </Alert>
          )}

          <Row>
            <Col md={6}>
              {activeTab === "upload" && (
                <ImageUploader onFileUpload={handleFileUpload} />
              )}

              {imageUrl && (
                <ImageDisplay
                  imageUrl={imageUrl}
                  title="Original Image"
                  hidden={activeTab !== "upload"}
                />
              )}

              {processedImageUrl &&
                (activeTab === "shape" || activeTab === "size") && (
                  <ImageDisplay
                    imageUrl={processedImageUrl}
                    title={
                      activeTab === "shape"
                        ? "Shape Detection Result"
                        : "Size Detection Result"
                    }
                  />
                )}

              {loading && (
                <div className="text-center mt-4">
                  <Spinner animation="border" role="status" variant="primary">
                    <span className="visually-hidden">Loading...</span>
                  </Spinner>
                  <p className="mt-2">Processing image...</p>
                </div>
              )}
            </Col>

            <Col md={6}>
              {activeTab === "shape" && shapes.length > 0 && (
                <ShapeResult shapes={shapes} />
              )}

              {activeTab === "size" && measurements.length > 0 && (
                <SizeResult measurements={measurements} />
              )}

              {activeTab === "upload" && imageUrl && (
                <div className="info-panel">
                  <h3>Instructions</h3>
                  <p>Now that you have uploaded an image, you can:</p>
                  <ul>
                    <li>
                      Click <strong>Detect Shape</strong> to identify shapes in
                      the image
                    </li>
                    <li>
                      Click <strong>Detect Size</strong> to measure object
                      dimensions
                    </li>
                  </ul>
                </div>
              )}
            </Col>
          </Row>
        </Card.Body>
        <Card.Footer className="text-center text-muted">
          <small>© 2023 Shape and Size Detector</small>
        </Card.Footer>
      </Card>
    </Container>
  );
}

export default App;
